import { createStore } from 'vuex'

export default createStore({
  state: {
    userIsAuthorized: false,
  },
  getters: {
  },
  mutations: {
    //changing user state programatically
    setUserIsAuthenticated(state, replacement){
      state.userIsAuthorized = replacement;
    }
  },
  actions: {
  },
  modules: {
  }
})
